INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (19.3197583,-99.3172788,'En la actualidad el Parque Nacional Desierto de los Leones con una superficie total de mil 529 hectáreas es un espacio natural hábitat de cinco especies endé­micas de mamíferos y 94 aves migratorias por mencionar parte de su riqueza natural. Además de sostener a un número de especies de flora y fauna esta área favorece la retención de la humedad y la recarga del acuífero previene la erosión contribuye a mejorar la calidad del aire.','Desierto de los Leones','https://www.gob.mx/cms/uploads/article/main_image/64728/33026532_10155201765320356_8714538834571821056_o.jpg',4);
INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (19.3536121,-99.2104478,'En este Parque Nacional se llevan a cabo actividades familiares culturales deportivas y recreativas. El mantenimiento y vigilancia de las instalaciones está a cargo de la Secretaría de Medio Ambiente y Recursos Naturales.','El Histórico Coyoacán','https://www.gob.mx/cms/uploads/article/main_image/25358/Blog_vivero.jpg',4);
INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (25.4895495,-100.5354512,'El Parque presenta desde zonas áridas con especies propias de las regiones desérticas pasando por matorrales con diversos tipos de vegetación hasta bosques principalmente de pinos y encinos en las partes más altas así como pastizales y diversas composiciones florísticas a lo largo de los ríos y cañadas.','Cumbres de Monterrey','https://www.gob.mx/cms/uploads/article/main_image/74415/cumbres_de_monterrey.jpg',4);
INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (29.3026273,-101.152187,'Alberga un extenso bosque de nogales que rodean a la laguna y al Arroyo de las Vacas; se incluyen poblaciones de encinos sauces y álamos así como las cactáceas típicas de la región entre ellas la biznaga las gobernadoras y las candelillas.','Los Novillos','https://www.gob.mx/cms/uploads/image/file/160633/LOS_NOVILLOS.jpg',4);
