INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (18.6696653,-99.5124489,'El Parque Nacional Grutas de Cacahuamilpa que se extiende al norte del estado de Guerrero sobre una superficie de 1598.26 hectáreas en los municipios de Pilcaya y Taxco de Alarcón fue decretada Parque Nacional en 1936 y alberga uno de los más sorprendentes y visitados sistemas de cuevas y formaciones calcáreas de la República Mexicana.','Grutas de Cacahuamilpa','https://www.gob.mx/cms/uploads/article/main_image/65059/29572509_1948383511870753_8001599469225472605_n.jpg',4);
