INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (20.1982325,-98.7184678,'El Parque Nacional El Chico presenta una singular belleza escénica por su relieve montañoso surcado por activo drenaje fluvial y recubierto con exuberante bosque de oyamel (Abies religiosa) en su conjunto conforma un mosaico natural que amalgama sobresalientes y distintivos rasgos físicos y biológicos plasmados en geoformas de gran belleza escénica como peñascos valles y cañadas.','El Chico','https://www.gob.mx/cms/uploads/article/main_image/66789/20-4.jpg',4);
