INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (19.424213,-100.1317747,'Esta reserva natural no cuenta con infraestructura turística formal sin embargo su atractivo ecológico radica en sus limpias veredas y paisajes inexplorados. Por esta razón las actividades preferentes son el excursionismo observación de vida silvestre campamentos y la pesca de mojarra y carpa. Otra de sus virtudes consiste en que parte de sus terrenos son santuario de la mariposa monarca.','Bosencheve','https://www.gob.mx/cms/uploads/article/main_image/22189/Bosencheve_.jpg',4);
INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (18.937032,-99.6046195,'Este lugar fue llamado "desierto" por ser un lugar de retiro ya que alberga las instalaciones del Convento del Carmen construido por la orden de los Carmelitas Descalzos en el siglo XVIII. Es considerado como patrimonio cultural histórico y religioso en la entidad.','Desierto del Carmen o de Nixcongo','https://www.gob.mx/cms/uploads/image/file/209877/Desierto3.jpeg',4);
