INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (21.8605867,-105.8744364,'Isla Isabel es un área de refugio alimentación apareamiento y anidación de diferentes especies de aves residentes y migratorias. Aquí se reproducen nueve especies de aves marinas en gran número entre ellas el bobo de Patas Azules el pelícano Café la fragata y la gaviota parda.','Isla Isabel','https://www.gob.mx/cms/uploads/article/main_image/29127/blog_isla_isabel.jpg',4);
INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (26.21740546,-99.75985835,'El Sabinal es un agradable destino recreativo y ecológico repleto de ahuehuetes árboles ligados a la cultura nacional desde la época prehispánica que llegan a vivir más de 500 años.  El parque cuenta con un centro comunitario de aprendizaje con cursos y talleres  e instalaciones turísticas básicas con alberca asadores palapas foros juegos y un recorrido al interior del parque en un pequeño tren.','El Sabinal','https://www.gob.mx/cms/uploads/article/main_image/23909/sabinal_gobmx.jpg',4);
