INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (15.74895846,-96.16364624,'Un paraíso enmarcado por las estribaciones de la Sierra Madre del Sur deslizándose suavemente bajo las aguas del mar; nueve bahías con vegetación de selva baja porciones de manglar un humedal temporal  y hermosas playas; frente a las dunas de arena los arrecifes de corales y rocas ofrecen sus caprichos submarinos.','Huatulco','https://www.gob.mx/cms/uploads/image/file/523815/GOPR8196_-_copia.jpg',4);
INSERT INTO lugar(Latitud,Longitud,Descripcion,Nombre,Imagenes,categorias_idCategorias) VALUES (17.11088746,-96.69553513,'En terrenos forestales conurbados a la ciudad de Oaxaca en los municipios de Oaxaca de Juárez San Andrés Huayapam y Tlalixtac de Cabrera se ubica este Parque Nacional. Cuenta con 2591 hectáreas que resguardan ecosistemas de bosque tropical caducifolio encino y pino hábitat de una amplia variedad de especies silvestres.','Benito Juárez','https://www.gob.mx/cms/uploads/article/main_image/29853/foto_BJ.jpeg',4);
